css:
.main{
    border: 2px solid black;
    color:black;
    font-size:12px;
    font-style:Georgia !important;
    height:100vh !important;

}

.main table{
    font-size:15px;
    color:black;
    font-style:Georgia !important;
}

.main tr{
    line-height: 100%;
    vertical-align: middle;
}

.main td{
    height:1px !important;
    padding: 4px !important;
    font-size:12px;
}

.head{
    border-top:2px solid black;
    border-bottom:2px solid black;
    overflow: hidden;
}

.head h4{
    text-align:center;
}

.letterhead{
    width:100%;
    padding:10px;
    overflow: hidden;
}

.letterhead1{
    width:50%;
    float: left;
    padding-top:15px;
}

.letterhead2{
    float: right;
}

.box1{
    padding-top:75px;
    padding-left:3px;
}

.table_a{
    width:100%;
    overflow: hidden;
    padding-bottom:10px;
}

.table1{
    width:50%;
    float:left;
    overflow: hidden;
}

.table1 table{
    width:100%;
}

.table2{
    float:right;
    width:50%;
    overflow: hidden;
}

.table2 table{
    width:100%;
}

.table_b{
    width:99.99%;
    border-top:2px solid black;
    border-bottom:2px solid black;
    overflow: hidden;
}

.earnings{
    float:left;
    width:50%;
    overflow: hidden;
    border-right: 2px solid black;
}

.earnings table{
    width:100%;
}

.deductions{
    float:right;
    width:50%;
    overflow: hidden;
}

.deductions table{
    width:100%;
}

.reimbursement{
    width:50%;
    float:left;
    overflow: hidden;
}

.reimbursement table{
    width:100%;
    border-right: 2px solid black;
    border-bottom: 2px solid black;

}

.other{
    width:50%;
    overflow: hidden;
    border-right: 2px solid black;
}

.other table{
    width:100%;
}

.box2{
    width:99.99%;
    overflow: hidden;
}

.bottombox{

    border-top:2px solid black;
    border-bottom:2px solid black;
    font-weight:bold;
    margin-bottom:25px;
    overflow: hidden;
}

.bottombox p{
    text-align:center;
    padding-top:5px;
    padding-bottom:5px;
}

.foot{
    width:100%;
    bottom:5px;
    position: absolute;
    text-align:center;
}
